    "id": "task_20260211_a89ocf",
    "title": "bill-bot: comprehensive warning system",
    "description": "",
    "type": "code",
    "status": "todo",
    "priority": "high",
    "project": "bill-bot-1oP-Cl",
    "sprint": "sprint-1-foundation-E8UFrB",
