arguments.command:
node - <<'NODE'
const { execSync } = require('child_process');
const OWNER='BillChirico';
const REPO='bills-bot';
function sh(cmd){ return execSync(cmd,{encoding:'utf8',stdio:['pipe','pipe','pipe']}); }
let prs=[];
try {
  prs = JSON.parse(sh(`gh pr list --repo ${OWNER}/${REPO} --state open --json number,title,headRefName,author,url`));
} catch (e) {
  console.error(e.stderr?.toString()||e.message);
  process.exit(1);
}
const skipped=[];
const eligible=[];
for(const pr of prs){
  const author=pr?.author?.login||'';
  if(/\[bot\]$/.test(author) || author==='coderabbitai' || author==='renovate'){
    skipped.push(pr);
    continue;
  }
  const query = `query($owner:String!, $name:String!, $number:Int!){ repository(owner:$owner, name:$name){ pullRequest(number:$number){ number title url reviewThreads(first:100){ nodes{ id isResolved path line comments(first:10){ nodes{ bodyText author{login} createdAt } } } } } } }`;
  const cmd = `gh api graphql -f owner=${OWNER} -f name=${REPO} -F number=${pr.number} -f query='${query.replace(/'/g,"'\\''")}'`;
  let gql;
  try{
    gql = JSON.parse(sh(cmd));
  } catch(e){
    console.error(`Failed PR #${pr.number}:`, e.stderr?.toString()||e.message);
    process.exit(1);
  }
  const prData = gql?.data?.repository?.pullRequest;
  const threads = prData?.reviewThreads?.nodes || [];
  const unresolvedThreads = threads
    .filter(t=>t && t.isResolved===false)
    .map(t=>{
      const body=(t.comments?.nodes?.[0]?.bodyText||'').replace(/\n+/g,' ').trim();
      return {
        id: t.id,
        filePath: t.path || null,
        line: t.line ?? null,
        issueSummary: body.slice(0,280)
      };
    });
  eligible.push({
    number: prData.number,
    title: prData.title,
    url: prData.url,
    unresolvedCount: unresolvedThreads.length,
    unresolvedThreads
  });
}
const result={all_prs:prs,skipped_prs:skipped,eligible_prs:eligible};
console.log(JSON.stringify(result,null,2));
NODE

