}

def run(cmd):
    p=subprocess.run(cmd,text=True,capture_output=True)
    if p.returncode!=0:
        raise RuntimeError(f"failed {' '.join(cmd)}\nstdout={p.stdout}\nstderr={p.stderr}")
    return p.stdout

def gh_json(cmd):
    out=run(['gh']+cmd)
    out=out.strip()
    return json.loads(out) if out else None

# list open PRs
open_prs=gh_json(['api',f'repos/{OWNER}/{REPO}/pulls?state=open&per_page=100'])
eligible=[]
skipped=[]
for pr in open_prs:
    author=pr['user']['login']
    if author in ALLOWED_AUTHORS:
        eligible.append(pr)
    else:
        skipped.append({'number':pr['number'],'author':author})

# GraphQL query for threads (paginate via cursor)
THREADS_QUERY='''
query($owner:String!, $repo:String!, $number:Int!, $cursor:String) {
  repository(owner:$owner, name:$repo) {
    pullRequest(number:$number) {
      reviewThreads(first:100, after:$cursor) {
        pageInfo { hasNextPage endCursor }
        nodes {
          id
          isResolved
          isOutdated
          path
          line
          originalLine
          comments(first:100) {
            nodes {
              id
              body
              createdAt
              updatedAt
              url
              author { login }
            }
          }
        }
      }
    }
  }
}
'''

# Very light actionable text heuristic for top-level review bodies
NON_ACTIONABLE_PATTERNS=[
    re.compile(r'^\s*lgtm\b', re.I),
    re.compile(r'no remaining findings', re.I),
    re.compile(r'0 remaining finding', re.I),
    re.compile(r'no issues found', re.I),
]

def body_actionable(body):
    b=(body or '').strip()
    if not b:
        return False
    for pat in NON_ACTIONABLE_PATTERNS:
        if pat.search(b):
            return False
    # if it mentions findings/issues/comments/actionable/fix, treat as actionable
    if re.search(r'\b(remaining|finding|findings|issue|issues|actionable|fix|must|should|warning|critical|nitpick|request(ed)? changes)\b', b, re.I):
        return True
    # fallback: long structured body likely actionable
    return len(b) > 160

