    'open_prs':[{'number':p['number'],'author':p['user']['login'],'head_ref':p['head']['ref'],'head_sha':p['head']['sha']} for p in open_prs],
    'eligible_prs':[p['number'] for p in eligible],
    'skipped_prs':skipped,
    'prs':{}
}

for pr in eligible:
    number=pr['number']
    head_sha=pr['head']['sha']

