        pageInfo { hasNextPage endCursor }
        nodes {
          id
          isResolved
          path
          line
          originalLine
          comments(first:100) {
            nodes {
              id
              body
              createdAt
              updatedAt
              url
              author { login }
            }
          }
        }
      }
    }
  }
}
'''

NON_ACTIONABLE=[
    re.compile(r'^\s*lgtm\b',re.I),
    re.compile(r'\bno remaining finding',re.I),
    re.compile(r'\b0 remaining finding',re.I),
    re.compile(r'\ball prior findings.*resolved',re.I),
]

def is_actionable_body(body):
    b=(body or '').strip()
    if not b:
        return False
    for pat in NON_ACTIONABLE:
        if pat.search(b):
            return False
    if re.search(r'\b(remaining|finding|findings|issue|issues|actionable|fix|must|should|warning|critical|nitpick|changes requested)\b', b, re.I):
        return True
    return len(b) > 180

out={
    'open_prs':[{'number':p['number'],'author':p['user']['login'],'head':p['head']['sha'],'ref':p['head']['ref']} for p in prs],
    'eligible':[p['number'] for p in eligible],
    'results':{}
}

for pr in eligible:
    num=pr['number']; head=pr['head']['sha']
    # paginate threads
    cursor=None; pages=0; unresolved=[]
    while True:
        args=['api','graphql','-f',f'query={THREADS_QUERY}','-F',f'owner={OWNER}','-F',f'repo={REPO}','-F',f'number={num}']
        if cursor:
            args += ['-F',f'cursor={cursor}']
        data=gh_json(args)
        pages += 1
        threads=data['data']['repository']['pullRequest']['reviewThreads']
        for t in threads['nodes']:
            if not t['isResolved']:
                comments=t.get('comments',{}).get('nodes',[]) or []
                created=min([c.get('createdAt') for c in comments if c.get('createdAt')] or ['9999'])
                last=comments[-1] if comments else {}
                unresolved.append({
                    'kind':'inline_thread',
                    'thread_id':t['id'],
                    'path':t.get('path'),
                    'line':t.get('line') or t.get('originalLine'),
                    'createdAt':created,
                    'url':last.get('url'),
                    'author':(last.get('author') or {}).get('login'),
                    'summary':(last.get('body') or '').strip().replace('\n',' ')[:220],
                })
        if not threads['pageInfo']['hasNextPage']:
            break
        cursor=threads['pageInfo']['endCursor']
    unresolved.sort(key=lambda x:x['createdAt'])

    reviews=gh_json(['api',f'repos/{OWNER}/{REPO}/pulls/{num}/reviews?per_page=100'])
    top=[]
    for r in reviews:
        state=r.get('state')
        if state not in ('CHANGES_REQUESTED','COMMENTED'):
            continue
        if r.get('commit_id') != head:
            continue
        body=r.get('body') or ''
        if not is_actionable_body(body):
            continue
        top.append({
            'kind':'high_level_review',
            'review_id':r.get('id'),
            'state':state,
            'author':(r.get('user') or {}).get('login'),
            'submitted_at':r.get('submitted_at'),
            'url':r.get('html_url'),
            'summary':body.strip().replace('\n',' ')[:240]
        })
    top.sort(key=lambda x:x.get('submitted_at') or '')

    items=sorted(unresolved+top, key=lambda i: i.get('createdAt') or i.get('submitted_at') or '9999')
    out['results'][str(num)]={
        'head':head,
        'ref':pr['head']['ref'],
        'thread_pages':pages,
        'unresolved_threads':len(unresolved),
        'high_level_actionable':len(top),
        'actionable_total':len(items),
        'items':items
    }

print(json.dumps(out,indent=2))
