import json, subprocess, sys, re
OWNER='BillChirico'
REPO='bills-bot'


def run(cmd):
    p=subprocess.run(cmd, shell=True, text=True, capture_output=True)
    if p.returncode!=0:
        print('CMD_FAIL', cmd, file=sys.stderr)
        print(p.stderr, file=sys.stderr)
        raise SystemExit(1)
    return p.stdout

# Fetch open PRs (paginate)
open_prs=[]
pr_cursor=None
while True:
    q='''query($owner:String!,$repo:String!,$cursor:String){repository(owner:$owner,name:$repo){pullRequests(states:OPEN,first:100,after:$cursor,orderBy:{field:UPDATED_AT,direction:DESC}){nodes{number title url author{login} headRefOid headRefName} pageInfo{hasNextPage endCursor}}}}'''
    args=[f"owner={OWNER}",f"repo={REPO}"]
    if pr_cursor:
        args.append(f"cursor={pr_cursor}")
    cmd="gh api graphql " + " ".join(["-F "+a for a in args]) + " -f query='"+q+"'"
    data=json.loads(run(cmd))
    prs=data['data']['repository']['pullRequests']
    open_prs.extend(prs['nodes'])
    if not prs['pageInfo']['hasNextPage']:
        break
    pr_cursor=prs['pageInfo']['endCursor']


def is_allowed_author(login:str)->bool:
    if not login:
        return False
    l=login.lower()
    if login=='BillChirico':
        return True
    # Pip Build / sub-agent naming variants
    allowed_exact={
        'pip-build','pipbuild','pip_build','pip-build[bot]','openclaw-build','openclaw-build[bot]'
    }
    if l in allowed_exact:
        return True
    if 'pip' in l and 'build' in l:
        return True
    return False

eligible=[pr for pr in open_prs if is_allowed_author((pr.get('author') or {}).get('login'))]

non_actionable_patterns=[
    r'^\s*lgtm\s*[!.]*\s*$',
    r'^\s*looks\s+good\s*[!.]*\s*$',
    r'^\s*nice\s+work\s*[!.]*\s*$',
    r'^\s*great\s+work\s*[!.]*\s*$',
    r'^\s*thanks\s*[!.]*\s*$',
]

def is_actionable(body:str)->bool:
    if not body:
        return False
    b=body.strip()
    if not b:
        return False
    for pat in non_actionable_patterns:
        if re.match(pat, b, flags=re.I|re.S):
            return False
    return True

report={'open_pr_count':len(open_prs),'eligible_pr_count':len(eligible),'eligible_prs':[]}

for pr in eligible:
    number=pr['number']
    head=pr['headRefOid']

    # review threads pagination
    unresolved=[]
    t_cursor=None
    while True:
        q='''query($owner:String!,$repo:String!,$number:Int!,$cursor:String){repository(owner:$owner,name:$repo){pullRequest(number:$number){reviewThreads(first:100,after:$cursor){nodes{id isResolved isOutdated path line originalLine startLine comments(first:100){nodes{id author{login} body url createdAt}}} pageInfo{hasNextPage endCursor}}}}}'''
        args=[f"owner={OWNER}",f"repo={REPO}",f"number={number}"]
        if t_cursor:
            args.append(f"cursor={t_cursor}")
        cmd="gh api graphql " + " ".join(["-F "+a for a in args]) + " -f query='"+q+"'"
        data=json.loads(run(cmd))
        threads=data['data']['repository']['pullRequest']['reviewThreads']
        for t in threads['nodes']:
            if not t['isResolved']:
                comments=t.get('comments',{}).get('nodes',[])
                summary=''
                if comments:
                    # latest non-empty comment body as issue summary
                    for c in reversed(comments):
                        b=(c.get('body') or '').strip()
                        if b:
                            summary=b
                            break
                unresolved.append({
                    'thread_id':t['id'],
                    'path':t.get('path'),
                    'line':t.get('line') or t.get('originalLine') or t.get('startLine'),
                    'is_outdated':t.get('isOutdated'),
                    'issue_summary':summary[:1000],
                    'comment_count':len(comments)
                })
        if not threads['pageInfo']['hasNextPage']:
            break
        t_cursor=threads['pageInfo']['endCursor']

    # top-level reviews (rest paginated)
    reviews=[]
    page=1
    while True:
        cmd=f"gh api repos/{OWNER}/{REPO}/pulls/{number}/reviews --paginate -f per_page=100"
        # Using --paginate already handles all pages in one output as concatenated JSON arrays (one per page)
        out=run(cmd)
        # Parse robustly: each line may be an array JSON
        items=[]
        for line in out.splitlines():
            line=line.strip()
            if not line:
                continue
            try:
                obj=json.loads(line)
            except Exception:
                continue
            if isinstance(obj,list):
                items.extend(obj)
            elif isinstance(obj,dict):
                items.append(obj)
        if not items and out.strip().startswith('['):
            try:
                arr=json.loads(out)
                if isinstance(arr,list):
                    items=arr
            except Exception:
                pass
        reviews=items
        break

    unaddressed=[]
    for r in reviews:
        state=(r.get('state') or '').upper()
        if state not in ('CHANGES_REQUESTED','COMMENT'):
            continue
        body=r.get('body') or ''
        if not is_actionable(body):
            continue
        rc=r.get('commit_id')
        # Treat as unaddressed only if no new commit exists since the review.
        if rc and head and rc!=head:
            continue
        unaddressed.append({
            'review_id':r.get('id'),
            'state':state,
            'user':(r.get('user') or {}).get('login'),
            'submitted_at':r.get('submitted_at'),
            'commit_id':rc,
            'body':body[:4000]
        })

    report['eligible_prs'].append({
        'number':number,
        'title':pr.get('title'),
        'url':pr.get('url'),
        'author':(pr.get('author') or {}).get('login'),
        'head':head,
        'unresolved_threads':unresolved,
        'unaddressed_high_level_reviews':unaddressed,
    })

print(json.dumps(report))
