cd /home/bill/.openclaw/workspace-build/bills-bot && gh api graphql -f query='query($number: Int!) {
  repository(owner: "BillChirico", name: "bills-bot") {
    pullRequest(number: $number) {
      reviewThreads(first: 50) {
        nodes {
          id
          isResolved
          comments(first: 50) {
            nodes {
              path
              line
              body
            }
          }
        }
      }
    }
  }
}' -F number=18 --jq '.data.repository.pullRequest.reviewThreads.nodes[] | select(.isResolved == false)'

