arguments.command:
python3 - <<'PY'
import json, subprocess, shlex, sys

def run(cmd):
    p = subprocess.run(cmd, shell=True, capture_output=True, text=True)
    if p.returncode != 0:
        print(p.stderr, file=sys.stderr)
        raise SystemExit(p.returncode)
    return p.stdout

open_prs = json.loads(run("gh pr list --state open --repo BillChirico/bills-bot --json number,title,author,headRefName,url"))
print(json.dumps({"open_prs": open_prs}, indent=2))

bot_names = {"coderabbitai", "renovate", "dependabot", "dependabot[bot]", "renovate[bot]"}
for pr in open_prs:
    login = (pr.get("author") or {}).get("login", "")
    num = pr["number"]
    if login.endswith("[bot]") or login in bot_names:
        print(f"SKIP_BOT_PR #{num} by {login}")
        continue

    query = '''query($owner:String!, $name:String!, $number:Int!) {
      repository(owner:$owner, name:$name) {
        pullRequest(number:$number) {
          reviewThreads(first:100) {
            nodes {
              id
              isResolved
              comments(first:1) {
                nodes {
                  path
                  line
                  body
                }
              }
            }
          }
        }
      }
    }'''

    cmd = (
        "gh api graphql "
        + " -f query=" + shlex.quote(query)
        + " -F owner=BillChirico -F name=bills-bot -F number=" + str(num)
    )
    data = json.loads(run(cmd))
    nodes = (((data.get("data") or {}).get("repository") or {}).get("pullRequest") or {}).get("reviewThreads", {}).get("nodes", [])
    unresolved = []
    for t in nodes:
        if t.get("isResolved") is True:
            continue
        c0 = ((t.get("comments") or {}).get("nodes") or [{}])[0]
        body = (c0.get("body") or "").replace("\n", " ").strip()
        unresolved.append({
            "threadId": t.get("id"),
            "path": c0.get("path") or "unknown",
            "line": c0.get("line"),
            "summary": body[:240],
        })

    print(f"ELIGIBLE_PR #{num} by {login} unresolved={len(unresolved)}")
    print(json.dumps(unresolved, indent=2))
