import json, subprocess, re
from textwrap import shorten

OWNER='BillChirico'
REPO='bills-bot'
ELIGIBLE_AUTHORS={'BillChirico','pip-build','pip-build[bot]','openclaw-pip-build','openclaw-pip-build[bot]'}


def gh_json(args, input_data=None):
    cmd=['gh']+args
    res=subprocess.run(cmd,input=input_data,text=True,capture_output=True)
    if res.returncode!=0:
        raise RuntimeError(f"gh failed: {' '.join(cmd)}\n{res.stderr}")
    out=res.stdout.strip()
    return json.loads(out) if out else None

# 1) list open PRs
prs=gh_json(['api','--method','GET',f'repos/{OWNER}/{REPO}/pulls','-f','state=open','-f','per_page=100'])
eligible=[]
for pr in prs:
    author=pr.get('user',{}).get('login')
    if author in ELIGIBLE_AUTHORS or author=='BillChirico' or ('pip' in (author or '').lower() and 'dependabot' not in (author or '').lower() and 'renovate' not in (author or '').lower() and 'coderabbit' not in (author or '').lower()):
        eligible.append({
            'number': pr['number'],
            'title': pr['title'],
            'author': author,
            'head': pr['head']['sha'],
            'url': pr['html_url']
        })

# actionable high-level body heuristic
non_actionable_re = re.compile(r'^\s*(lgtm|looks good( to me)?|nice work|great work|thanks!?|thank you!?|approved|ship it|nit: ?none)\s*[.!]*\s*$', re.I)

def is_actionable(text):
    if not text or not text.strip():
        return False
    t=text.strip()
    if non_actionable_re.match(t):
        return False
    return True

results=[]
for pr in eligible:
    number=pr['number']
    head=pr['head']

    # 3a unresolved inline review threads with pagination
    unresolved=[]
    cursor=None
    while True:
        query='''
query($owner:String!, $name:String!, $number:Int!, $cursor:String){
  repository(owner:$owner,name:$name){
    pullRequest(number:$number){
      reviewThreads(first:100, after:$cursor){
        nodes{
          id
          isResolved
          isOutdated
          path
          line
          originalLine
          startLine
          diffSide
          comments(first:100){
            nodes{
              id
              author{ login }
              body
              createdAt
              url
            }
          }
        }
        pageInfo{ hasNextPage endCursor }
      }
    }
  }
}
'''
        vars={'owner':OWNER,'name':REPO,'number':number,'cursor':cursor}
        payload={'query':query,'variables':vars}
        data=gh_json(['api','graphql','--input','-'], input_data=json.dumps(payload))
        rt=data['data']['repository']['pullRequest']['reviewThreads']
        for t in rt['nodes']:
            if not t.get('isResolved'):
                comments=t.get('comments',{}).get('nodes',[])
                summary=''
                if comments:
                    summary=shorten(comments[-1].get('body','').replace('\n',' '), width=220, placeholder='…')
                unresolved.append({
                    'thread_id': t.get('id'),
                    'path': t.get('path'),
                    'line': t.get('line') or t.get('originalLine') or t.get('startLine'),
                    'is_outdated': t.get('isOutdated'),
                    'summary': summary,
                    'url': comments[-1].get('url') if comments else None
                })
        if not rt['pageInfo']['hasNextPage']:
            break
        cursor=rt['pageInfo']['endCursor']

    # 3b high-level reviews
    reviews=gh_json(['api','--method','GET',f'repos/{OWNER}/{REPO}/pulls/{number}/reviews','-f','per_page=100'])
    unaddressed=[]
    for r in reviews:
        state=r.get('state')
        body=r.get('body') or ''
        if state not in ('CHANGES_REQUESTED','COMMENT'):
            continue
        if not is_actionable(body):
            continue
        review_commit=r.get('commit_id')
        has_new_commits = (review_commit is not None and review_commit != head)
        if has_new_commits:
            continue
        unaddressed.append({
            'review_id': r.get('id'),
            'state': state,
            'user': r.get('user',{}).get('login'),
            'submitted_at': r.get('submitted_at'),
            'commit_id': review_commit,
            'body': body.strip(),
            'html_url': r.get('html_url')
        })

    results.append({
        **pr,
        'unresolved_threads': unresolved,
        'unaddressed_high_level': unaddressed
    })

print(json.dumps({'eligible_prs':results}, indent=2))
