set -euo pipefail
cd /home/bill/.openclaw/workspace-build/bills-bot
node <<'NODE'
const { execSync } = require('child_process');

function run(cmd) {
  return execSync(cmd, { encoding: 'utf8', stdio: ['pipe', 'pipe', 'pipe'] });
}

const prsRaw = run('gh pr list --state open --repo BillChirico/bills-bot --json number,title,author,headRefName,url');
const prs = JSON.parse(prsRaw);

const out = [];
for (const pr of prs) {
  const author = (pr.author && pr.author.login) || '';
  const eligible = !(/\[bot\]$/.test(author) || author === 'coderabbitai' || author === 'renovate');

  const query = `query($owner:String!, $name:String!, $number:Int!){repository(owner:$owner,name:$name){pullRequest(number:$number){reviewThreads(first:100){nodes{id isResolved path line startLine comments(first:1){nodes{body author{login}}}}}}}}`;
  const cmd = [
    'gh api graphql',
    '-f', `query=${JSON.stringify(query)}`,
    '-F', 'owner=BillChirico',
    '-F', 'name=bills-bot',
    '-F', `number=${pr.number}`,
  ].join(' ');

  const threadsResp = JSON.parse(run(cmd));
  const nodes = threadsResp?.data?.repository?.pullRequest?.reviewThreads?.nodes || [];
  const unresolved = nodes
    .filter((t) => t.isResolved === false)
    .map((t) => {
      let body = t?.comments?.nodes?.[0]?.body || '';
      body = body.replace(/\r/g, ' ').replace(/\n/g, ' ').trim();
      if (body.length > 220) body = body.slice(0, 220) + '‚Ä¶';
      return {
        threadId: t.id,
        filePath: t.path || '',
        line: t.line ?? t.startLine ?? null,
        issueSummary: body,
      };
    });

  out.push({
    number: pr.number,
    title: pr.title,
    url: pr.url,
    branch: pr.headRefName,
    author,
    eligible,
    unresolved,
  });
}

process.stdout.write(JSON.stringify(out, null, 2));
