python3 - <<'PY'
import json, subprocess, shlex

REPO = 'BillChirico/bills-bot'

def run(cmd):
    p = subprocess.run(cmd, stdout=subprocess.PIPE, stderr=subprocess.PIPE, text=True)
    if p.returncode != 0:
        raise RuntimeError(f"Command failed: {' '.join(cmd)}\n{p.stderr}")
    return p.stdout

open_prs = json.loads(run(['gh','pr','list','--repo',REPO,'--state','open','--json','number,title,headRefName,author,url']))

def is_bot(author_login: str) -> bool:
    if not author_login:
        return False
    return author_login.endswith('[bot]') or author_login in {'coderabbitai','renovate'}

eligible = [pr for pr in open_prs if not is_bot((pr.get('author') or {}).get('login',''))]

query = '''query($owner:String!, $name:String!, $number:Int!) {
  repository(owner:$owner, name:$name) {
    pullRequest(number:$number) {
      reviewThreads(first:100) {
        nodes {
          id
          isResolved
          path
          line
          startLine
          comments(first:1) {
            nodes {
              body
              author { login }
              url
            }
          }
        }
      }
    }
  }
}'''

analysis = []
for pr in eligible:
    number = pr['number']
    raw = run([
        'gh','api','graphql',
        '-f',f'query={query}',
        '-f','owner=BillChirico',
        '-f','name=bills-bot',
        '-F',f'number={number}'
    ])
    data = json.loads(raw)
    nodes = data['data']['repository']['pullRequest']['reviewThreads']['nodes'] if data.get('data') else []
    unresolved = [t for t in nodes if not t.get('isResolved')]
    threads = []
    for t in unresolved:
        c0 = ((t.get('comments') or {}).get('nodes') or [{}])[0]
        body = (c0.get('body') or '').replace('\n',' ').strip()
        body = ' '.join(body.split())
        threads.append({
            'threadId': t.get('id'),
            'filePath': t.get('path') or 'unknown',
            'line': t.get('line') or t.get('startLine') or 0,
            'issueSummary': body[:240]
        })
    analysis.append({
        'number': number,
        'title': pr.get('title'),
        'branch': pr.get('headRefName'),
        'author': (pr.get('author') or {}).get('login',''),
        'url': pr.get('url'),
        'unresolvedCount': len(unresolved),
        'threads': threads,
    })

print(json.dumps({'openPRs': open_prs, 'eligiblePRs': eligible, 'analysis': analysis}))
